concepts.codeExample:
#include <stdio.h>

int main() {
    int age = 25;
    float height = 5.9;

    printf("Size of int: %lu bytes\n", sizeof(int));
    printf("Size of float: %lu bytes\n", sizeof(float));
    printf("Address of age: %p\n", (void*)&age);
    printf("Address of height: %p\n", (void*)&height);

    return 0;
}

codeEditor.defaultCode:
#include <stdio.h>

int main() {
    int age = 25;
    float gpa = 3.85;
    char grade = 'A';

    printf("Age: %d\n", age);
    printf("GPA: %.2f\n", gpa);
    printf("Grade: %c\n", grade);

    return 0;
}

