concepts.codeExample:
int age = 25;

if (age >= 18) {
    printf("You are an adult\n");
} else {
    printf("You are a minor\n");
}

if (age < 13) {
    printf("Child\n");
} else if (age < 18) {
    printf("Teenager\n");
} else if (age < 65) {
    printf("Adult\n");
} else {
    printf("Senior\n");
}

char grade = 'B';

switch (grade) {
    case 'A':
        printf("Excellent!\n");
        break;
    case 'B':
        printf("Good!\n");
        break;
    case 'C':
        printf("Average\n");
        break;
    default:
        printf("Invalid grade\n");
}

// Output: Good!

codeEditor.defaultCode:
#include <stdio.h>

int main() {
    int score = 85;

    if (score >= 90) {
        printf("Grade: A\n");
    } else if (score >= 80) {
        printf("Grade: B\n");
    } else if (score >= 70) {
        printf("Grade: C\n");
    } else {
        printf("Grade: F\n");
    }

    return 0;
}

