concepts.codeExample:
// Without loop - repetitive
printf("1 ");
printf("2 ");
printf("3 ");

// With loop - clean
for(int i = 1; i <= 3; i++) {
    printf("%d ", i);
}

// Print numbers 0 to 9
for(int i = 0; i < 10; i++) {
    printf("%d ", i);
}

// Iterate through array
int arr[5] = {1, 2, 3, 4, 5};
for(int i = 0; i < 5; i++) {
    printf("%d ", arr[i]);
}

// Nested loops - times table
for(int i = 1; i <= 3; i++) {
    for(int j = 1; j <= 3; j++) {
        printf("%d ", i * j);
    }
    printf("\n");
}

int x = 5;
while(x > 0) {
    printf("%d ", x);
    x--;
}
// Output: 5 4 3 2 1

// Reading user input until valid
int age = -1;
while(age < 0 || age > 150) {
    printf("Enter your age: ");
    scanf("%d", &age);
}

// Continue until sentinel value
int num;
while(scanf("%d", &num) == 1 && num != -1) {
    printf("You entered: %d\n", num);
}

