#include <stdio.h>

int main() {
    // For loop: multiplication table
    printf("Multiplication table of 5:\n");
    for(int i = 1; i <= 10; i++) {
        printf("5 x %d = %d\n", i, 5 * i);
    }

    return 0;
}

challenges.solution:
for(int i = 1; i <= 5; i++) {
    for(int j = 0; j < i; j++) {
        printf("*");
    }
    printf("\n");
}

int arr[5] = {10, 20, 30, 40, 50};
int sum = 0;
for(int i = 0; i < 5; i++) {
    sum += arr[i];
}
printf("Sum: %d\n", sum);
