concepts.codeExample:
int scores[5] = {85, 90, 78, 92, 88};
int first = scores[0];   // 85
int third = scores[2];   // 78
scores[1] = 95;          // Change second element

char letters[3] = {'a', 'b', 'c'};
printf("%c\n", letters[0]);  // Prints: a

int arr[5] = {10, 20, 30, 40, 50};
int sum = 0;

// Calculate sum
for (int i = 0; i < 5; i++) {
    sum += arr[i];
}
printf("Sum: %d\n", sum);  // Sum: 150

// Print all elements
for (int i = 0; i < 5; i++) {
    printf("%d ", arr[i]);
}  // Prints: 10 20 30 40 50

int matrix[2][3] = {
    {1, 2, 3},
    {4, 5, 6}
};

// Access element at row 1, column 2
int element = matrix[1][2];  // 6

// Iterate through matrix
for (int i = 0; i < 2; i++) {
    for (int j = 0; j < 3; j++) {
        printf("%d ", matrix[i][j]);
    }
    printf("\n");
}

codeEditor.defaultCode:
#include <stdio.h>

int main() {
    int numbers[5] = {10, 20, 30, 40, 50};

    printf("First element: %d\n", numbers[0]);
    printf("Third element: %d\n", numbers[2]);

    for (int i = 0; i < 5; i++) {
        printf("numbers[%d] = %d\n", i, numbers[i]);
    }

    return 0;
}

