concepts.codeExample:
// Declare strings
char name[20] = "Alice";
char greeting[] = "Hello, World!";

// String literals
printf("%s\n", name);           // Prints: Alice
printf("%s\n", greeting);       // Prints: Hello, World!

// Character arrays
char word[6] = {'W', 'o', 'r', 'l', 'd', '\0'};
printf("%s\n", word);           // Prints: World

#include <string.h>
#include <stdio.h>

char str1[20] = "Hello";
char str2[20] = "World";
char result[40];

int len = strlen(str1);          // 5
strcpy(result, str1);            // result = "Hello"
strcat(result, " ");             // result = "Hello "
strcat(result, str2);            // result = "Hello World"
int cmp = strcmp(str1, "Hello");  // 0 (equal)

printf("%s\n", result);           // Prints: Hello World

#include <stdio.h>
#include <string.h>

char name[50];
char message[100];

printf("Enter your name: ");
scanf("%49s", name);  // Safe: at most 49 chars for 50-element array

printf("Enter a message: ");
fgets(message, 100, stdin);  // Safe: reads at most 99 chars

printf("Hello, %s!\n", name);
printf("Your message: %s", message);

codeEditor.defaultCode:
#include <stdio.h>
#include <string.h>

int main() {
    char greeting[30] = "Hello";
    char name[20];

    printf("Enter your name: ");
    scanf("%19s", name);

    strcat(greeting, " ");
    strcat(greeting, name);

    printf("%s\n", greeting);
    printf("Length: %lu\n", strlen(greeting));

    return 0;
}

