concepts.codeExample:
struct Person {
    int age;
    float height;
    char initial;
};

struct Person person1;
person1.age = 25;
person1.height = 5.9;
person1.initial = 'A';

printf("Age: %d\n", person1.age);
printf("Height: %.1f\n", person1.height);
printf("Initial: %c\n", person1.initial);

struct Person {
    int age;
    float height;
};

struct Person people[3] = {
    {25, 5.9},
    {30, 6.1},
    {22, 5.5}
};

// Access elements
printf("%d\n", people[0].age);  // 25
people[1].height = 6.2;

// Iterate through array
for (int i = 0; i < 3; i++) {
    printf("Person %d: age=%d, height=%.1f\n", i, people[i].age, people[i].height);
}

struct Person { int age; float height; };

void printPerson(struct Person p) {
    printf("Age: %d, Height: %.1f\n", p.age, p.height);
}

void changePerson(struct Person *p, int newAge) {
    p->age = newAge;
}

struct Person createPerson(int a, float h) {
    struct Person p = {a, h};
    return p;
}

int main() {
    struct Person p = createPerson(25, 5.9);
    printPerson(p);
    changePerson(&p, 26);
    printPerson(p);
}

