#include <stdio.h>

struct Student {
    int id;
    float gpa;
    char grade;
};

int main() {
    struct Student s1 = {101, 3.85, 'A'};
    struct Student s2 = {102, 3.60, 'B'};

    printf("Student %d: GPA=%.2f, Grade=%c\n", s1.id, s1.gpa, s1.grade);
    printf("Student %d: GPA=%.2f, Grade=%c\n", s2.id, s2.gpa, s2.grade);

    return 0;
}

